mapper.select:
select `link`.`ID`,`directory`.`dirname`,`link`.`picPath`,`link`.`value`,`link`.`read`,`link`.`title`,`link`.`source`,`link`.`time`,`link`.`type` 
from `link` inner join `directory` on 
`link`.`dirID`=`directory`.`ID` 
and `directory`.`dirname`=#{1}
and `directory`.`username` =#{0}
limit #{2},6;

select `link`.`ID`,`directory`.`dirname`,`link`.`picPath`,`link`.`value`,`link`.`read`,`link`.`title`,`link`.`source`,`link`.`time`,`link`.`type` 
from `link` inner join `directory` on 
`link`.`dirID`=`directory`.`ID` 
and `directory`.`username` =#{0}
limit #{1},6;
